<?phpif ( ! defined( 'ABSPATH' ) ) {	exit;}?><?php global $show_content, $show_price; ?><div class="property-small">    <figure class="property-small-image">		<?php if ( has_post_thumbnail() ) :   ?>			<?php the_post_thumbnail( 'large' ); ?>		<?php endif; ?>    </figure>    <div class="property-small-content ptb-10 prl-20">        <div class="property-small-title">            <h5 class="property-small-head"><a href="<?php the_permalink(); ?>"><?php the_title() ?></a></h5>		    <?php $price = Realia_Price::get_property_price(); ?>		    <?php if ( ! empty( $price ) && $show_price == 'yes' ) : ?>                <h5 class="property-small-price"><?php echo wp_kses( $price, wp_kses_allowed_html( 'post' ) ); ?></h5>		    <?php endif; ?>        </div>        <?php $location = Realia_Query::get_property_location_name(); ?>        <?php if ( ! empty( $location ) ) : ?>            <p class="property-small-location mb-0"><?php echo wp_kses( $location, wp_kses_allowed_html( 'post' ) ); ?></p>        <?php endif; ?>    </div></div>